sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	loadList ('axwayMFT_getBUs','business_unit');

//	loadList ('axwayMFT_getBUs','decryption_certificate');

//	loadList ('axwayMFT_getBUs','remote_protocol');

	loadList ('axwayMFT_getSysCerts','system_certificate');

//---------
// loadList
//---------
function loadList(end_point,field_name)	{
	var sm, response, responseBody, status, requestBody;

	try {
		sm = new sn_ws.RESTMessageV2(end_point, 'load_bu'); //method

		response = sm.executeAsync();
		response.waitForResponse(10);
		responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
		status = response.getStatusCode();
	} catch(ex) {
		responseBody = ex.getMessage();
		status = '500';
	} finally {
		requestBody = sm ? sm.getRequestBody() : null;
	}

	var InfoMessage = [];
	InfoMessage.push("*** "+end_point+" HTTP status: " + status);
	InfoMessage.push("*** "+end_point+" HTTP Response: " + responseBody);
	InfoMessage.push("*** "+end_point+" HTTP Request Body: " + requestBody);
	gs.info(InfoMessage.join('\n'));

	if (status != 200 && status != 201) {
		gs.addErrorMessage("Cannot load "+end_point+":<br />"+InfoMessage.join('<br />'));
		current.setAbortAction(true);
	}

	var created_item = [], created_certid = [], API_list = JSON.parse(response.getBody());
	API_list = API_list.result;

	for(var i in API_list) {
		var key, key1;
		if (end_point == 'axwayMFT_getBUs')
			key = API_list[i]["name"];
		else
//			key = API_list[i]["name"];
//			key1 = API_list[i]["name"]+"_"+API_list[i]["id"];
			key = API_list[i]["id"];
		doChoiceList(field_name,key,API_list[i]["name"]);
		created_item.push(key);
	}

	doDeactivateItems(field_name,created_item);
}

//-------------
// doChoiceList
//-------------
function doChoiceList(field, value, label) {
	var table = 'x_806049_axway_mft_axway_mftaccounts';
	var sys_choice = new GlideRecord('sys_choice');
	sys_choice.addQuery('name','=',table);
	sys_choice.addQuery('element','=',field);
	sys_choice.addQuery('value','=',value);
	sys_choice.query();
	if (!sys_choice.next()) {
		sys_choice.inactive = false;
		sys_choice.name = 'x_806049_axway_mft_axway_mftaccounts';
		sys_choice.element = field;
		sys_choice.value = value;
		sys_choice.label = label;
		sys_choice.insert();
	} else {
		sys_choice.inactive = false;
		sys_choice.label = label;
		sys_choice.update();
	}	
}

//------------------
// doDeactivateItems
//------------------
function doDeactivateItems(field,list) {
	var table = 'x_806049_axway_mft_axway_mftaccounts';
	var sys_choice = new GlideRecord('sys_choice');
	sys_choice.addQuery('name','=',table);
	sys_choice.addQuery('element','=',field);
	var EncodedQuery = "value!="+list.join("^value!=");
	sys_choice.addEncodedQuery(EncodedQuery);
	sys_choice.query();
	while (sys_choice.next()) {
		sys_choice.inactive = true;
		sys_choice.update();
	}
}

})(current, previous);
