sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	var requestBody, responseBody, status, sm;
	var requestBody1, responseBody1, status1, sm1;
	var methodname;

	if ((current.communication_profile == "client") || (current.communication_profile == "both")) {
		methodname = "create_client";
	} else {
		methodname = "create_account";
	}

	try {
	sm = new sn_ws.RESTMessageV2('axwayMFT_partners', methodname); // method for account creation

	sm.setStringParameter("partner_name", current.partner_name);
	sm.setStringParameter("type", current.type);
	sm.setStringParameter("bu", current.business_unit);
//	sm.setStringParameter("homedir", current.business_unit);
	sm.setStringParameter("mail", current.email_contact);	
	sm.setStringParameter("phone", current.phone_contact);
	sm.setStringParameter("uname", current.user_id);
	sm.setStringParameter("password", current.password);

	response = sm.executeAsync();
	responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
	status = response.getStatusCode();
	} catch(ex) {
		responseBody = ex.getNessage();
		status = '500';
	} finally {
		requestBody = sm ? sm.getRequestBody():null;
	}

	var InfoMessage = [];

	if ((current.communication_profile == "server") || (current.communication_profile == "both")) {

		sm1 = new sn_ws.RESTMessageV2('axwayMFT_Sites', 'ssh_site'); // method

		sm1.setStringParameter("partner_name", current.partner_name);
		sm1.setStringParameter("remotehost", current.remote_server);
		sm1.setStringParameter("remoteport", current.remote_port);
		sm1.setStringParameter("remoteuser", current.remote_userid);
		sm1.setStringParameter("remotepass", current.remote_password);
		sm1.setStringParameter("uploadpath", current.remote_upload_path);
		sm1.setStringParameter("downloadpath", current.remote_download_path);

		response1 = sm1.execute();
		responseBody1 = response.haveError() ? response.getErrorMessage() : response.getBody();
		status1 = response.getStatusCode();

		InfoMessage.push("*** BI MFT Onboardibng HTTP Status: " + status);
		InfoMessage.push("*** BI MFT Onboardibng HTTP Response: " + responseBody);
		InfoMessage.push("*** BI MFT Site Creation HTTP Status: " + status1);
		InfoMessage.push("*** BI MFT Site Creation HTTP Response: " + responseBody1);
		gs.info(InfoMessage.join('\n'));		

	} else {

		InfoMessage.push("*** BI MFT Onboardibng HTTP Status: " + status);
		InfoMessage.push("*** BI MFT Onboardibng HTTP Response: " + responseBody);
		InfoMessage.push("*** BI MFT Onboardibng HTTP Request Body: " + requestBody);
		gs.info(InfoMessage.join('\n'));	
	}

	if (status != 200 && status != 201 && status != 202) {
		gs.addErrorMessage("Cannot "+current.operation()+" record due due to API call:<br />"+InfoMessage.join('<br /><br />'));
	} else {
		gs.addErrorMessage("Partner On-boarded successfully:<br />"+InfoMessage.join('<br /><br />'));
	}


})(current, previous);
